$Script = {

$MsSQL1_iSCSI_IQN = (Get-InitiatorPort).NodeAddress

$PrismIP = "@@{Prism_Element_Cluster_IP}@@"
$PrismUser = "@@{Nutanix_Prism_Creds.username}@@"
$PrismPass = "@@{Nutanix_Prism_Creds.secret}@@"

add-type @"
    using System.Net;
    using System.Security.Cryptography.X509Certificates;
    public class TrustAllCertsPolicy : ICertificatePolicy {
        public bool CheckValidationResult(
            ServicePoint srvPoint, X509Certificate certificate,
            WebRequest request, int certificateProblem) {
            return true;
        }
    }
"@
[System.Net.ServicePointManager]::CertificatePolicy = New-Object TrustAllCertsPolicy

[Net.ServicePointManager]::SecurityProtocol = [Net.SecurityProtocolType]::Tls12

$BaseURL = "https://" + $PrismIP + ":9440/PrismGateway/services/rest/v2.0/"
$Header = @{"Authorization" = "Basic "+[System.Convert]::ToBase64String([System.Text.Encoding]::UTF8.GetBytes($PrismUser+":"+$PrismPass))}
$Type = "application/json"

$VolumeGroupURL = $BaseURL+"volume_groups"
$ContainerURL = $BaseURL+"storage_containers"

$ContainerList = invoke-RestMethod -Uri $ContainerURL -Method Get -TimeoutSec 100 -Headers $Header -ContentType $Type
$ContainerUUID = $ContainerList.entities | where { $_.name -eq "@@{Nutanix_Container_Name}@@"} | select storage_container_uuid
$ContainerUUID = $ContainerUUID.storage_container_uuid

$DB_DiskSize = @@{DB_Disk_Size_GB}@@*1024*1024*1024
$Log_DiskSize = @@{Log_Disk_Size_GB}@@*1024*1024*1024

$Body = @"
{
  "disk_list": [
    {
      "create_config": {
        "size": 1073741824,
	"storage_container_uuid": "$ContainerUUID"
      },
      "index": 0
    },
    {
      "create_config": {
        "size": $DB_DiskSize,
	"storage_container_uuid": "$ContainerUUID"
      },
      "index": 1
    },    
    {
      "create_config": {
        "size": $Log_DiskSize,
	"storage_container_uuid": "$ContainerUUID"
      },
      "index": 2
    }
  ],
  "is_shared": true,
  "name": "@@{Volume_GroupName}@@"
}
"@

Invoke-RestMethod -Uri $VolumeGroupURL -Method Post -TimeoutSec 100 -Headers $Header -ContentType $Type -Body $Body

}

$password = "@@{Domain_Admin.secret}@@" | ConvertTo-SecureString -asPlainText -Force
$Creds = New-Object System.Management.Automation.PSCredential("@@{Domain_Admin.username}@@",$password)

Invoke-Command -ComputerName @@{address}@@ -Credential $Creds -ScriptBlock $script -Authentication Credssp

