$password = "@@{Domain_Admin.secret}@@" | ConvertTo-SecureString -asPlainText -Force
$Creds = New-Object System.Management.Automation.PSCredential("@@{Domain_Admin.username}@@",$password)

Invoke-Command -ComputerName @@{address}@@ -Credential $Creds -ScriptBlock $script -Authentication Credssp

$Script = {

New-IscsiTargetPortal -TargetPortalAddress @@{Prism_Element_DataServices_IP}@@
Get-IscsiTarget | Connect-IscsiTarget -IsPersistent $true


#Initialize Disks
Get-Disk -Number 1 | Initialize-Disk -ErrorAction SilentlyContinue
Get-Disk -Number 2 | Initialize-Disk -ErrorAction SilentlyContinue
Get-Disk -Number 3 | Initialize-Disk -ErrorAction SilentlyContinue

#Format Witness Disk
foreach ($disk in get-disk){
$disksize = ($disk).size
if (($disksize /1024 /1024 /1024) -eq 1){
	New-Partition -DiskNumber ($disk).Number -UseMaximumSize -DriveLetter Q -ErrorAction SilentlyContinue | Format-Volume -Confirm:$false -FileSystem NTFS -NewFileSystemLabel "Witness" }
}

#Format Data Disk
foreach ($disk in get-disk){
$disksize = ($disk).size
if (($disksize /1024 /1024 /1024) -eq @@{DB_Disk_Size_GB}@@){
	New-Partition -DiskNumber ($disk).Number -UseMaximumSize -DriveLetter E -ErrorAction SilentlyContinue | Format-Volume -Confirm:$false -FileSystem NTFS -NewFileSystemLabel "Data" }
}

#Format Logs Disk
foreach ($disk in get-disk){
$disksize = ($disk).size
if (($disksize /1024 /1024 /1024) -eq @@{Log_Disk_Size_GB}@@){
	New-Partition -DiskNumber ($disk).Number -UseMaximumSize -DriveLetter F -ErrorAction SilentlyContinue | Format-Volume -Confirm:$false -FileSystem NTFS -NewFileSystemLabel "Logs" }
}

}

$password = "@@{Domain_Admin.secret}@@" | ConvertTo-SecureString -asPlainText -Force
$Creds = New-Object System.Management.Automation.PSCredential("@@{Domain_Admin.username}@@",$password)

Invoke-Command -ComputerName @@{address}@@ -Credential $Creds -ScriptBlock $script -Authentication Credssp

$Script = {

$NodeList = "@@{MSSQL1_VMName}@@","@@{MSSQL2_VMName}@@"

ForEach ($Node in $NodeList){
Install-WindowsFeature -Name Failover-Clustering -IncludeManagementTools -ComputerName "$Node"
}

}

$password = "@@{Domain_Admin.secret}@@" | ConvertTo-SecureString -asPlainText -Force
$Creds = New-Object System.Management.Automation.PSCredential("@@{Domain_Admin.username}@@",$password)

Invoke-Command -ComputerName @@{address}@@ -Credential $Creds -ScriptBlock $script -Authentication Credssp

$Script = {

Test-Cluster -node @@{MSSQL1_VMName}@@,@@{MSSQL2_VMName}@@
New-Cluster -Name @@{WSFC_Cluster_Name}@@ -Node @@{MSSQL1_VMName}@@,@@{MSSQL2_VMName}@@ -StaticAddress @@{WSFC_Cluster_IP}@@

#Rename Cluster Disks
$ClusterDisks =  Get-CimInstance -ClassName MSCluster_Resource -Namespace root/mscluster -Filter "type = 'Physical Disk'"
foreach ($Disk in $ClusterDisks) {
$DiskResource = Get-CimAssociatedInstance -InputObject $Disk -ResultClass MSCluster_DiskPartition
    if (-not ($DiskResource.VolumeLabel -eq $Disk.Name)) {
    Invoke-CimMethod -InputObject $Disk -MethodName Rename -Arguments @{newName = $DiskResource.VolumeLabel}
    }
}

}

