def get_az_external_subnet_uuid(az_pc_ip,az_pc_username,az_pc_password,external_subnet_name):
  api_url = 'https://' + az_pc_ip + ':9440/api/nutanix/v3/subnets/list'
  headers = {'Content-Type': 'application/json', 'Accept': 'application/json'}

  payload = {
      'kind': 'subnet'
  }

  r = urlreq(api_url, verb='POST', params=json.dumps(payload), headers=headers, verify=False, auth="BASIC" ,user=az_pc_username, passwd=az_pc_password)
  if r.ok:
    resp = json.loads(r.content)
  else:
    print("Function get_az_external_subnet_uuid Post Request Failed", r.content)
    exit(1)

  for subnet in resp['entities']:
    if subnet['spec']['name'] == external_subnet_name:
      #print(subnet['metadata']['uuid'])
      return subnet['metadata']['uuid']

def getVmNicUuid(AzPC_IP,username,password,vmName):
    api_url = 'https://' + AzPC_IP + ':9440/api/nutanix/v3/vms/list'
    headers = {'Content-Type': 'application/json', 'Accept': 'application/json'}

    payload = {
        "kind": "vm",
        "filter": "vm_name=={}".format(vmName)
    }

    r = urlreq(api_url, verb='POST', params=json.dumps(payload), headers=headers, verify=False, auth="BASIC", user=username, passwd=password)
    if r.ok:
        resp = json.loads(r.content)
        print(resp['entities'][0]['status']['resources']['nic_list'][0]['uuid'])
        return resp['entities'][0]['status']['resources']['nic_list'][0]['uuid']

def assignFloatingIp(AzPC_IP,username,password,vmNicUuid,VpcExternal_subnet_reference):

    api_url = 'https://' + AzPC_IP + ':9440/api/nutanix/v3/floating_ips'
    headers = {'Content-Type': 'application/json', 'Accept': 'application/json'}

    payload = {
        "spec": {
            "resources": {
                "vm_nic_reference": {
                    "kind": "vm_nic",
                    "uuid": vmNicUuid
                },
                "external_subnet_reference": {
                    "kind": "subnet",
                    "uuid": VpcExternal_subnet_reference
                }
            }
        },
        "api_version": "3.1.0",
        "metadata": {
            "kind": "floating_ip"
        }
    }

    r = urlreq(api_url, verb='POST', params=json.dumps(payload), headers=headers, verify=False, auth="BASIC", user=username, passwd=password)
    if r.ok:
        resp = json.loads(r.content)
        task_uuid = resp['status']['execution_context']['task_uuid']

    else:
        print("Post request failed", r.content)
        exit(1)

    state = ""
    while state != "SUCCEEDED":
        api_url = 'https://' + AzPC_IP + ':9440/api/nutanix/v3/tasks/{}'.format(task_uuid)

        sleep(2)
        r = urlreq(api_url, verb='GET', params=json.dumps(payload), headers=headers, verify=False, auth="BASIC", user=username, passwd=password)
        if r.ok:
            resp = json.loads(r.content)
            state = resp['status']
            print(resp)
            if state == "FAILED":
                print("Task failed", resp['progress_message'])
                exit(1)

        else:
            print("Post request failed", r.content)
            exit(1)

    floatingIpUuid = resp['entity_reference_list'][0]['uuid']

    api_url = 'https://' + AzPC_IP + ':9440/api/nutanix/v3/floating_ips/{}'.format(floatingIpUuid)

    r = urlreq(api_url, verb='GET', params=json.dumps(payload), headers=headers, verify=False, auth="BASIC", user=username, passwd=password)
    if r.ok:
        resp = json.loads(r.content)
        floatingIpAddress = resp['status']['resources']['floating_ip']
        return floatingIpAddress



